    private void jAgeKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jAgeKeyTyped
        char c = evt.getKeyChar();
        if (!Character.isDigit(c)) { // Si no es número, cancelar el evento
            evt.consume();
        }
    }//GEN-LAST:event_jAgeKeyTyped

