    private void searchByDNIActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchByDNIActionPerformed
        StudentManager.removeStudent(jTextField1.getText());
        jTextArea1.append(StudentManager.showRegister());
    }//GEN-LAST:event_searchByDNIActionPerformed

